import styled from 'styled-components';

const Text = styled.p`
    font-family: "RobotoBold", sans-serif;
    font-size: 1.25rem;
    line-height: 1.25rem;
    font-weight: 600;
    color: #131220;
    text-align: center;
`;

export default Text;
