import styled from 'styled-components';

export const Label = styled.label`
    margin-bottom: 5px;
    font-family: "RobotoBold", sans-serif;
    font-size: 0.875rem;
    line-height: 1rem;
    font-weight: 600;
    color: #131220;
`;
